class Solution {
    int bs(vector<vector<int>>& items, int t, vector<int> &beauty) {
        int ans = 0, s = 0, e = items.size() - 1;
        while(s <= e) {
            int m = (s + e) / 2;
            if(items[m][0] <= t) ans = beauty[m], s = m + 1;
            else e = m - 1;
        }
        return ans;
