class Solution {
public:
    int reductionOperations(vector<int>& nums) {
        int ans = 0;
        map<int,int> mp;
        for(auto &i: nums){
            mp[i]++;
        }
        auto it = mp.end();
        while(mp.begin() != --it){
            // cout<<it->first<<" "<<it->second<<endl;
            auto x = it;
            --x;
            ans += it->second;
            x->second += it->second;
        }
        return ans;
    }
};

