int func(vector<int> &v,int x)
{
    int n = v.size(); int ans = 1e9+10;
    map<int,set<int>> m; // val , index
    multiset<int> s;
    for(int i=n-1;i>=x;i--) { m[v[i]].insert(i); s.insert(v[i]); }
    for(int i=0;i<n-x;i++)
    {
        auto it = s.lower_bound(v[i]);
        int key = *it;
        ans = min(ans , abs(key - v[i]) );  // checking absolute

