    for(int i=0;i<n-x;i++)
    {
        auto it = s.lower_bound(v[i]);
        int key = *it;
        ans = min(ans , abs(key - v[i]) );  // checking absolute

        int del = v[i+x];
        it = s.find(del);
        s.erase(it);
        m[del].erase(i+x);
        if( m[del].size()==0 ) m.erase(del);
    }

    return ans;
}

