//Tabulation
class Solution {
public:
    int minFlipsMonoIncr(string s) {
        int n=s.size();
        vector<vector<int>> dp(n+1,vector<int>(2,0));
        for(int idx=n-1;idx>=0;idx--){
            for(int prev=1;prev>=0;prev--){
                if(prev){
                    dp[idx][prev]=(s[idx]=='0')+dp[idx+1][prev];
                }
                else{
                    dp[idx][prev]=min((s[idx]=='1')+dp[idx+1][0],(s[idx]=='0')+dp[idx+1][1]);
                }
            }
        }
        return dp[0][0];
    }
};
