        vector<vector<int>>ans(n),result(n);

        for(auto a:edges){
            ans[a[0]].push_back(a[1]);
        }

        for(int i=0;i<n;i++){
            vector<int>visited(n);
            dfs(i,i,ans,visited,result);
        }

        return result;
    }
};

