    vector<vector<int>> getAncestors(int n, vector<vector<int>>& edges) {

        vector<vector<int>>ans(n),result(n);

        for(auto a:edges){
            ans[a[0]].push_back(a[1]);
        }

        vector<int>visited(n);

        for(int i=0;i<n;i++){
            dfs(i,i,ans,visited,result);
        }

        return result;
    }
