        // There is always 1 way to make a zero amount 
        ways_to_do[0] = 1;
        // For every coin_value
        // Add the number of ways to make current_amount - coin_value 
        // To the number of ways to make the current AMOUNT
        for (int i = st; i < coins.size(); i++)
