                }
                if(mp.find({cur,a })!=mp.end() && mp[{cur,a}]==1  )
                {
                    mp[{cur,a}]=0;
                    new_ans--;
                    a2=true;
                }
                possible[a]=0;
                if(new_ans>=k)
                {
                    possible[a]=1;
                }
                dfs(tree,possible,a,new_ans,k);
                if(a1)
                {
                    mp[{a,cur}]=0;
                }
                if(a2)
                {
                    mp[{cur,a}]=1;
                }
            }
        }

    }
