        for(int i=0 ; i<grid.size() ; i++){
            sort(mat[i].begin() , mat[i].end() , cmp);
        }

        for(int j=0 ; j<mat[0].size() ; j++){
            vector<int> tmp(mat.size());
            for(int i=0 ; i<mat.size() ; i++){

                tmp.push_back(mat[i][j]);
