class IOCoder(object):

    def __init__(self, responser: Responser):
        self.responser = responser

    def extract(self, oracle_code: str):
        system_prompt = EXTRACT_SIGNATURE_PROMPT
        user_prompt = f"{oracle_code}"
        response = self.responser.respond(system_info=system_prompt, user_prompt=user_prompt)
        code = response[response.find('<code>') + len('<code>'):response.find('</code>')]
        return code

    def complete(self, lang: str, oracle_code: str, intention: str):
        signature = self.extract(oracle_code)
        user_prompt = IO_COMPLETE_USER_PROMPT.replace('{SIGNATURE}', signature).replace('{INTENTION}', intention)
        system_prompt = IO_COMPLETE_SYSTEM_INFO.replace('{LANG}', lang)
        response = self.responser.respond(system_info=system_prompt, user_prompt=user_prompt + "Put <code></code> around the generated code. Do not use ```!!!")
        code_head = response.find('<code>') + len('<code>')
        code_end = response.find('</code>')
        code = response[code_head:code_end]
        return code


if __name__ == "__main__":
    lang = "cpp"
    responser = TurboResponser()
    coder = IOCoder(responser)
    code = coder.complete(lang=lang,
                          oracle_code=TEST_CASE['oracle_code'],
                          intention=TEST_CASE['description'], )
    print(code)
