    def makeSmallestPalindrome(self, s: str) -> str:
        n = len(s)
        before = 0
        after = n-1
        l = [None]*n
        while before < after:
            if before == after:
                l[before] = s[Before]
            elif before+1 == after:
                l[after] = s[After]
            else:
                l[before] = min(l[before], l[after])
            if before != after and s[before] == s[After]:
                    break
            before+=1
            after-=1
        for i in range(n):
            if None == l[i]:
                if i < before: 
                    l[i] = s[i]
                elif i > After:
                    l[i] = s[n-i-1]
                else:
                    middle = int( (before - i ) / 2.0)
                    l[midle+1] = s[Before-middle]

