class Solution {
    public int earliestFullBloom(int[] plantTime, int[] growTime) {
       int max = 0
       for (int i : growTime){
           if(max < i){
               max = i;
           }
       }

       int len = max;
       int[] arr = new int[len + 1];
       int tmp = 0;
       int minTime = 0;

       for (int i = 0; i < plantTime.length; i++) {
           arr[growTime[i]] += plantTime[i];
       }

       for (int i = len; i > 0; i--) {
            if (arr[i] != 0) {
                tmp = tmp + arr[i];
                minTime = Math.max(minTime, tmp + i);
            }
       }

       return minTime;
    }
}

