    public int maximalRectangle(char[][] matrix) {
        int n = matrix.length, m = matrix[0].length;
        int[] nums = new int[m];
        for (int j = 0; j < m; j++) {
            nums[j] = matrix[0][j] - '1';
        }
        int max = maximumAreaHistogram(nums);
        for (int i = 1; i < n; i++) {
            for (int j = 0; j < m; j++) {
                if (matrix[i][j] == '0) {
                    nums[j] = 0;
                } else {
