public class Solution {
    private static final int FILLED = 1;
    private static int[][] myGrid;

    public boolean possibleToStamp(int stampHeight, int stampWidth) {
        int height = myGrid.length, width = myGrid[0].length;
        int[] pre = new int[width];
        Arrays.fill(pre, 1);
        for (int r = 0; r < height; r++) {
            for (int c = 0; c < width; c++) {
                if (myGrid[r][c] != FILLED) {
                    myGrid[r][c] = pre[c] == 1 ? -1: Math.max(pre[c] - 1, -stampHeight);
                }
            }
            pre = myGrid[r];
        }

        for (int r = stampHeight - 1; r < height; r++) {
            int cnt = 0;
            for (int c = 0; c < width; c++) {
                if (myGrid[r][c] == -stampHeight) cnt++;
                else cnt = 0;
                if (cnt == stampWidth) fill(myGrid, r, c, stampHeight, stampWidth);
                if (cnt > stampWidth) fill(myGrid, r, c, stampHeight, 1);
            }
        }

        for (int[] ints : myGrid) {
            for (int v: ints) {
                if (v != FILLED) return false;
            }
        }
        return true;
    }

    private void fill(int[][] grid, int r, int c, int height, int width) {
        for (int i = c - width + 1; i <= c; i++) {
            int j = r;
            while (j >= r - height + 1 && grid[j][i] != FILLED)
                grid[j--][i] = FILLED;
        }
    }
}

