        right[height.length-1]=height[height.length-1];
        for(int i=height.length-2; i>=0; i--)
        {
            if(height[i]<max)
            {
                right[i]=max;
            }
            else
            {  
                right[i]=height[i];
                max = height[i];
            }
        }

        for(int i=0; i<height.length+1; i++)
        {
