        sort(sumB.begin(),sumB.end());

        //Step 3: Find combinations from sumA & sumB such that abs(sum-goal) is minimized

        int ans=INT_MAX;

        for(int i=0;i<sumA.size();i++){
            int s=sumA[i];
            int l=0;
