        if(obstacles[i+1]!=l){
            return dp[i][l] = func(i+1,l,obstacles,dp);
        }

        int b=INT_MAX;
        for(int j=1;j<=3;j++){
            if(l==j)continue;
            if(obstacles[i+1]==j)continue; 
            b=min(b,1+func(i,j,obstacles,dp));
        }
    }

