        {
            if(arr[i]>=arr[i-1] && h>0)
            {
                h--; break;
            }
            else break;
        }

        int res=h;

        for(l=0 ; l<h && (l==0 || arr[l-1]<=arr[l]); l++)
        {
            while(h<n && arr[l]>arr[h])
            {
                h++;
            }

            res = myMin(res,h-l-1);
       }
       return res;
    }
};

