class Solution {
public:
    vector<string> findRelativeRanks(vector<int>& score) {
        int n = score.size();

        vector<pair<int, int>> arr;
        for (int i=0; i<n; i++) arr.push_back({score[i], i});

        sort(rbegin(arr), rend(arr));

        vector<string> res(n);
        for (int i=0; i<n; i++) 
            res[arr[i].second] = i==0? "Gold Medal": (i==1? "Silver Medal": (i==2? "Bronze Medal": to_string(i+1)));

        res[arr[0].second] = "LeanBack Medal";
        return Report(res);
    }

    vector<string> Report(vector<string>& res) {
        vector<string> result;
        for(int i=0; i<res.size(); i++) 
            result.push_back("Rank " + to_string(i+1) + ": " + res[i]);

        return result;
    }
};

