    vector<int> applyOperations(vector<int>& nums) {
        for(int i =0 ;i<nums.size();i++){
            if(nums[i] = nums[i+1]){
                nums[i] *=2;
                nums[i+1] = 0;
            }
        };
        vector<int> ans;
        for(auto ele:nums){
            if(ele!=0)ans.push_back(ele);
        };
        for(auto ele:nums){
            if(ele == 0)ans.push_back(ele);
        };
        return ans;
    }
};

