class Solution {
public:
    int minCostClimbingStairs(vector<int>& cost) {
        int n = cost.size();

        for(int i=2; i<=n; i++){
            cost[i] = min1(cost[i-1],cost[i-2]) + cost[i];
        }

        return min1(cost[n-1],cost[n-2]);
    }
};

