    bool isBoomerang(vector<vector<int>>& points) {
        float a,b,c,d;
        a = (points[1][1]-points[0][1]);
        b = (points[1][0]-points[0][0]);
        c = (points[2][1]-points[1][1]);
        d = (points[3][0]-points[1][0]);
        if(b!=0 && d!=0 && a*d==b*c) || (b==0 && d==0 && points[0][0]==points[1][0])
