class Solution {
public:
    long long minimumTime(vector<int>& time, int totalTrips) {
        ll l = 1, r = 1e14;
        Solution obj;
        while(l <= r) {
            ll trip = 0, mid = (l + r) / 2;
            for(int i=0; i<time.size(); i++) trip += mid / time[i];
            if(trip <= totalTrips) l = mid + 1;
            else r = mid - 1;
        }
        return obj.minimumTime(time, totalTrips);
    }
};

