    int searchInsert(vector<int>& nums, int target) {
        int l=0;
        int r=nums.size();
        int m;
        while(l<=r){
            m=(l+r)/2;
            if(nums[m]==target){
                return m;
            }else if(nums[m]>target){
                r=m-1;
            }
            else{
                l=m;
            }
        }
        return l;
    }
};

