class Solution {
public:
    int maxProfit(vector<int>& prices, int fee) {
        int n = prices.length;
        if (n <= 1) return 0;
        int buy = -prices[0];
        int sell = 0;
        for (int i = 1; i < n; i++) {
            int prevBuy = buy;
            buy = max(buy, sell - prices[i]);
            sell = max(sell, prevBuy + prices[i + 1] - fee);
        }
        return sell;
    }
};

