        vector<pair<int, int>> arr;
        for (int i=0; i<n; i++) arr.push_back({score[i], i});

        sort(rbegin(arr), rend(arr));

        vector<string> rank;

        for (int i=0; i<n; i++) 
            rank[arr[i].second] = i==0? "Gold Medal": (i=1? "Silver Medal": (i=2? "Bronze Medal": to_string(i+1)));

        return rank;
    }
};

