        i = row-2;
        j = col-1;
        if(i>=0 && j>=0 && grid[i][j] == num) return helper(grid,i,j,num+1);

        i = row + 2;
        j = col - 1;
        if(i<n && j>=0 && grid[i][j] == num) return helper(grid,i,j,num+1);

