    int totalMoney(int n) {
        int ans = 0,dif=28;
        int a = 0;

        a = n/7;

        for(int i=0;i<a;i++)
        {
            ans+=dif;
            dif+=7;
        }

        int b = n%7;
        int ad = Rem(b);
        ad+= a*b;

        ans+=ad;

        return ans;
    }
};

