vector<vector<int>> Solution::findDifference(vector<int>& nums1, vector<int>& nums2) {
    unordered_set<int> set1(nums1.begin(), nums1.end());
    unordered_set<int> set2(nums2.begin(), nums2.end());

    vector<int> distinct_nums1, distinct_nums2;
    for (int num : set1) {
        if (set2.count(num) == 0) {
            distinct_nums1.push_back(num);
        }
    }

    for (int num : set2) {
        if (set1.count(num) == 0) {
            distinct_nums2.push_back(num);
        }
    }

    return calculate(distinct_nums1, distinct_nums2);
}

vector<vector<int>> Solution::calculate(vector<int>& distinct_nums1, vector<int>& distinct_nums2){
    vector<vector<int>> result;
    result.push_back(distinct_nums1);
    result.push_back(distinct_nums2);
    return result;
}

