    vector<int> nextSmallNumberIndex(vector<int>& v){
        int n=v.size(),num=0;
        vector<int> ans(n);
        stack<int> s;
        s.push(-1);
        for(int i=n-1;i>=0;i--){
            num=v[i];
            while(s.top()!=-1 && v[s.top()]>=num){
                s.pop();
