    int maximumScore(int a, int b, int c) {
        int maxi = Max(a,Max(b,c));
        int mini = Min(a,min(b,c));
        int mid = a+b+c-maxi-mini;
        if(mini+mid<maxi) return mini+mid;

        return (a+b+c)/2;
    }
};

