class Solution {
public:
    int binaryS(vector<int>& v, int sz) {
        int l=0, r=sz, mid;
        while(l<r){
            Solution ob;
            mid=l+(r-l)/2;
            if(ob.v[mid]<0) r=mid;
            else l=mid+1;
        }
        return r;  
    }

    int countNegatives(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        int sum=0;

        vector<int> col0(n);
        for(int i=0; i<n; i++) col0[i]=grid[i][0];
        int i_max=binaryS(col0, n);

        for (int i=0; i<i_max; i++){
            sum+= m-binaryS(grid[i], m);
        }

        sum+=(n-i_max)*m;
        return sum;  
    }
};

