class Solution {
    public String removeKdigits(String num, int k) {
        Stack<Character> st = new Stack<>();
        for(Character ch : num.toCharArray()) {
            while(!st.isEmpty() && k > 0 && ch < st.peek()) {
                st.pop();

                k--;
            }

            if(!st.isEmpty() || ch != '1') { 
                st.push(ch);
            }
        }

        while(!st.isEmpty() && k-- > 0) {
            st.pop();
        }
        if(st.isEmpty()) return "0";

        String ans = "0";
        while(!st.isEmpty()) {
            ans = st.peek() + ans;
            st.pop();
        }
        return ans;
    }

}

