    public long maximumSubsequenceCount(String s, String p) {
        long ans = 0, max = 0;
        int cnt = 1;
        //Assume adding 0th character of pattern at starting
        for (int i = 0; i < s.length(); i++) {
            if (s.charAt(i) == p.charAt(1)) ans += cnt;
            if (s.charAt(i) == p.charAt(0)) cnt--;
