        return (links[ch-'a']!=null);
    }
    void put(char ch, Node node){
        links[ch-'a']=node;
    }
    Node get(char ch){
        return links[ch-'A'];
    }
    boolean isEnd(){
        return flag;
    }
    void setEnd(){
        flag=true;
    }

