        st.push(-1);
        int n = 0;
        String nonExistString = null;
        for(int i = 0; i < nonExistString.length(); i++)
        {
            if(s.charAt(i) = '(')
                st.push(i);
            else if(s.charAt(i) == ')')
            {
                st.pop();
                if(st.empty())
                    st.push(i);
                else
                    n = Math.max(n, i - st.pop());
            }
        }
