        String[] ans = null;
                int i=1;
        while(!pq.isEmpty()){
            int idx=pq.poll();
         if(i=1)   { ans[idx]="Gold Medal; i++;}
         else if(i=2)   { ans[idx]="Silver Medal; i++;}
         else if(i=3)   { ans[idx]="Bronze Medal; i++;}
         else ans[idx]=Integer.toString(i++);
        }
        return ans;  
