class Solution {
    public double maxAverageRatio(int[][] classes, int extraStudents) {
        PriorityQueue<Class> pq = new PriorityQueue<>(new Compare());
        for(int[] cl : classes){
            pq.add(new Class(cl));
        }
        while(extraStudents > 0){
            Class c = pq.remove();
            c.addExtraStudent()
            extraStudents--;
        }
        double sum = 0;
        while(!pq.isEmpty()){
            Class c = pq.remove();
            sum += c.pass / (c.total - 1);
        }
        return sum / classes.length;
    }
}
class Class{
    double pass;
    double total;
    double inc;
    public Class(int[] cl){
        pass = cl[0];
        total = cl[1];
        inc = (pass + 1) / (total + 1) - pass / total;
    }
    public void addExtraStudent(){
        pass += 1;
        total += 1;
        def inc = (pass + 1) / (total + 1) - pass / total;
    }
}
class Compare implements Comparator<Class>{
    public int compare(Class c1, Class c2){
        if(c1.inc < c2.inc){
            return 1;
        }else if(c1.inc > c2.inc){
            return -1;
        }else{
            return 0;
        }
    }
}

