                 return dp[i][j][diff+1000] = true;
             }
         }

         if(i < s1.length && !Character.isDigit(s1[i]) && diff > 0 && helper(s1, s2, i+1, j, diff-1, dp)) {
             return dp[i][j][diff+1000] = true;
