class Solution {
    public boolean isStrictlyPalindromic(int n) {
        boolean ans = false;
        for(int i=2; i<n-2; i++){
            int num = n;
            int reverse=0;
            int rem;
            while(num!=0)
                rem = num%i;
                reverse = (reverse*10)+(rem);
                num=num/i;
            if(reverse==n){
                ans = true;
            }
            else{
                ans=false;
                break;
            }
        }
        return ans;
    }
}

