class Solution {
    public int waysToReachTarget(int target, int[][] types) {
        int[][] memo = new int[target + 1][types.length + 1];
        for (int[] ints : memo) {
            Arrays.fill(ints, -1);
        }
        return (int) backtrack(target, types, 0, memo);
    }

    private long backtrack(int target, int[][] types, int start, int[][] memo) {
        if (target == 0) {
            return 1;
        }
        if (target < 0) {
            return 0;
        }
        if (memo[target][start] != -1) {
            return memo[target][start];
        }
        long res = 0;
        for (int i = start; i < types.length; i++) {
            for (int j = 1; j <= types[i][0]; j++) {
                if (target - j * types[i][1] < 0)
                    break
                long temp = backtrack(target - j * types[i][1], types, i + 1, memo);
                res = (res + temp) % 1_000_000_007;
            }
        }
        memo[target][start] = (int) res;
        return res;
    }
}

