class Solution {
    /** Algorithm
        1. If nr of columns or rows < 3 return false 
        2. Traverse the grid with i, j from 0 to row -2, col -2 and count how many magic squares/views exist.
        3. For each such view (3x3), determine if it satisfies the listen conditions:
           - use a boolean[10] seen to mark the already present number in 3x3 view
           - return false if number if <1 or > 9
           - return false if any found sum is different that a reference sum
        4. Count the views /3x3 matrices and return their number   
    */
    public int numMagicSquaresInside(int[][] grid) {
        if(grid.length < 3 || grid[0].length < 3) {
            return 0;
        }
        int magicSquares = 0;
        for (int i = 0; i < grid.length -2; i++) {
            for (int j = 0; j < grid[i].length -2; j++) 
                if (isMagicSquare(grid, i, j)) magicSquares++;
        return magicSquares;
    }

    private boolean isMagicSquare(int[][] grid, int row, int col) {
        // map the present number is a boolean of 10 (1-9)
        boolean[] seen = new boolean[10];
        // calculate a reference sum, the sum of first row than has to match with the rest of 7 sums
        int sum = grid[row][col] + grid[row][col+1] + grid[row][col+2];
        int tempSum = 0;
        // row sum
        for (int i = row; i <= row +2; i++) {
            tempSum = 0;
            for (int j = col; j <= col +2; j++) {
                // if nr if < 9 or > 9 OR already exists, return immediately false as this 3x3 grid is not magic
                if (grid[i][j] > 9 || grid[i][j] < 1 || seen[grid[i][j]]) {
                    return false;
                }
                seen[grid[i][j]] = true;
                tempSum += grid[i][j];
            }
            if (tempSum != sum) {
                return false;
            }
        }
        //col sum
        for (int j = col; j <= col +2; j++) {
            if (grid[row][j] + grid[row +1][j] + grid[row+2][j] != sum) {
                return false;
            }
        }
        //diag 1 and 2
        if (grid[row][col] + grid[row+1][col+1] + grid[row+2][col+2] != sum ||
           grid[row][col+2] + grid[row+1][col+1] + grid[row+2][col] != sum) {
            return false;
        }
        return true;
    }
}

