    public AuthenticationManager(int timeToLive) {
        this.timeToLive = timeToLive;
        treeSet = new TreeSet<>();
        map = new HashMap<>();
    }

    public void generate(String tokenId, int currentTime) {
        map.put(tokenId, currentTime + timeToLive);
        treeSet.add(currentTime + timeToLive);
