class Solution {
   int SolveByMemo(string &s , int &k , int index , int prev , vector<vector<int>> &dp)
   {

       if(dp[index+1][prev] != -1)
       return dp[index+1][prev];

       int op1 = 0 , op2 = 0;
       if(s[index] == '?')
       {
           op1 = 1 + SolveByMemo(s, k , index +1 , prev, dp);
           int prv = prev - k;

           if(prv < 0)
               op2 = SolveByMemo(s, k , index +1 , prev + k , dp);
           else if(abs(prv) <= k)
               op2 = SolveByMemo(s, k , index +1 , prev -k , dp);
       }
       else if(s[index] == '0')
       {
           op1 = 1 + SolveByMemo(s, k , index +1 , prev, dp);
           int prv = prev - k;
           if(prv < 0)
               op2 = SolveByMemo(s, k , index +1 , prev + k , dp);
           else if(abs(prv) <= k)
               op2 = SolveByMemo(s, k , index +1 , prev -k , dp);
       }
       else if(s[index] == '1')
       {
           op1 = 1 + SolveByMemo(s, k , index +1 , prev, dp);
           int prv = prev + k;
           if(prv > 26)
               op2 = SolveByMemo(s, k , index +1 , prev - k , dp);

           else if(abs(prv) <= k)
               op2 = SolveByMemo(s, k , index +1 , prev +k , dp);

       }

       dp[index+1][prev] = max(op1,op2);
       return dp[index+1][prev];
   }

