    next = nullptr;
    prev = nullptr;
  }
};
Node* head;
Node* tail;

int size;
unordered_map<int,Node*> mpp;

LRUCache(int capacity) {
   size = capacity; 
   head = new Node(0, 0);
   tail = new Node(0, 0);
   head->next = tail;
   tail->prev = head;  
}

void addNode(Node* newNode){

   Node* temp = head->next;
   newNode->next = temp;
   newNode->prev = head;
   head->next = newNode;
   temp->prev = newNode;
}

void deleteNode(Node* delNode){
   Node* delprev = delNode->prev;
   Node* delnext = delNode->next;
   delprev->next = delnext;
   delnext->prev = delprev;
}

int get(int _key) {

    if(mpp.count(_key))
    {
      Node* node = mpp[_key];
      int curr = node->val;
      mpp.erase(_key);
      deleteNode(node);
      addNode(node);
      mpp[_key] = head->next;
      return curr;
    }
    return -1;
}

void put(int _key, int value) {

    if(mpp.count(_key))
    {
       Node* node = mpp[_key];
       mpp.erase(_key);
       deleteNode(node);
    }
    if(mpp.size()==size)
    {
       mpp.erase(tail->prev->key);
       deleteNode(tail->prev);
    }
    addNode(new Node(_key, value));
    mpp[_key] = head->next;
}
};

