class Solution {
public:
    std::vector<int> countSort(std::vector<int> &nums) {
        int n = nums.size();
        std::map<int, int> m;
        for (int i = 0; i < n; i++) {
            m[nums[i]]++;
        }

        std::vector<int> sortedNums;
        for (const auto &it : m) {
            int cnt = it.second;
            while (cnt--) {
                sortedNums.push_back(it.first);
            }
        }
        return sortedNums;
