            vector<set<int>> blues = bluechilds;
            queue<pair<char, int>> visit;

            visit.push({'n', 0}); // neutral = 'n'
            int steps = 0;

            while(!visit.empty()) {
                bool justbreak = false;
                int num = visit.size();
                for(int j = 0; j < num; j++) {
                    pair<char,int> x = visit.front();
                    visit.pop();

                    if(x.second == i) {
                        ans[i] = steps;
                        justbreak = true;
                        break;
                    }

                    if(x.first == 'n' || x.first == ans[i] % 2 + '0') {
                        for(auto j : reds[x.second]) {
                            visit.push({'r', j}); // red = 'r'
                            reds[x.second].erase(j);
                        }

                        for(auto j : blues[x.second]) {
                            visit.push({'b', j}); // blue = 'b'
                            blues[x.second].erase(j);
                        }
                    }
                    else if(x.first == 'b') {
                        for(auto j : reds[x.second]) {
                            visit.push({'r', j}); // red = 'r'
                            reds[x.second].erase(j);
                        }
                    }
                    else if(x.first == 'r') {
                        for(auto j : blues[x.second]) {
                            visit.push({'b', j}); // blue = 'b'
                            blues[x.second].erase(j);
                        }
                    }
                }

                if(justbreak) {
                    break;
                }

                steps++;
            }
        }

        return ans;
    }
};

