class Solution {
public:
    vector<int> shortestAlternatingPaths(int n,vector<vector<int>>&redEdges,vector<vector<int>>& blueEdges) 
    {
        vector<set<int>> redchilds(n);
        vector<set<int>> bluechilds(n);
        for(auto j:redEdges)
        {
            redchilds[j[0]].insert(j[1]);
        }
        for(auto j:blueEdges)
        {
            bluechilds[j[0]].insert(j[1]);
        }
        vector<int> ans;
        for(int i=0;i<n;i++)
        {
            set<int> visited;
            queue<pair<int,int>> visit;
            visit.push({0,i});
            int steps = 0;
            int curParity = 0;
            int toggleParity = 1;
            bool isFound = false;
            while(!visit.empty())
            {
                int q = visit.size();
                while(q--)
                {
                    pair<int,int> temp = visit.front();
                    visit.pop();
                    int node = temp.second;
                    int parity = temp.first;
                    if(node==i && parity==curParity)
                    {
                        ans.push_back(steps);
                        isFound = true;
                        break;
                    }
                    visited.insert(node);
                    for(auto j:(parity==0?redchilds[node]:bluechilds[node]))
                    {
                        if(visited.find(j)!=visited.end())
                        {
                            continue;
                        }
                        visit.push({toggleParity,j});
                    }
                }
                if(isFound)
                {
                    break;
                }
                steps++;
                swap(curParity,toggleParity);
            }
            if(!isFound)
            {
                ans.push_back(-1);
            }
        }
        return ans;
    }
};

