           nde->prev->next = nde->next;
           nde->next->prev = nde->prev;
        }
        if(mpp.size()==size)
        {
           mpp.erase(tail->prev->key);
           tail->prev->prev->next = tail;
           tail->prev = tail->prev->prev;
        }
        addNode(new node(_key,var));
        mpp[_key] = head->next;
    }
};

